import React from "react";

import { LoadingOutlined, PlusOutlined } from '@ant-design/icons';
import { message, Upload, Form } from 'antd';

import changeImgCss from "./changeImg.module.css";
import { httpReq } from "../../../tool/httpReq";
import { useState } from "react";



export default function ChangeImg(props) {
  //状态初始化
  const [state, setState] = useState({
    img: "",
  });
  const handleChange = (info) => {
    if (info.file.status === 'uploading') {
      // setLoading(true);
      return;
    }

    // if (info.file.status === 'done') {
    //   // Get this url from response in real world.
    //   // getBase64(info.file.originFileObj, (url) => {
    //     // setLoading(false);
    //     // setImageUrl(url);
    //   });
    // }
  }
  const beforeUpload = (file) => {
    const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';
  
    if (!isJpgOrPng) {
      message.error('You can only upload JPG/PNG file!');
    }
  
    const isLt2M = file.size / 1024 / 1024 < 2;
  
    if (!isLt2M) {
      message.error('Image must smaller than 2MB!');
    }
  
    return isJpgOrPng && isLt2M;
  };
  
  //上传图片
  function upload() {
    console.log(1);
    // const formData = new FormData();
    // formData.append("file", state.img);
    // httpReq("post", `/static/Image/cover?columnId=1`, formData).then(
    //   (res) => {
    //     console.log(res);
    //   },
    //   (err) => console.log(err)
    // );
  }

  return (
    <div className={changeImgCss.changeImg}>
      <div className={changeImgCss.preImg}>
        <div>原图片:</div>
        <img
          width={"110px"}
          height={"111px"}
          src={props.imgUrl}
          alt="nosource"
        />
        <div></div>
      </div>

      <div className={changeImgCss.operation}>
        <Form onFinish={upload}>
          <Form.Item name="file">
            <Upload
              name="file"
              beforeUpload={beforeUpload}
              onChange={handleChange}
            >

            </Upload>
          </Form.Item>
        </Form>
        <div>
          <div onClick={upload}>上传</div>
          <div
            onClick={function () {
              props.setState((pre) => {
                return { ...pre, isUpload: false };
              });
            }}
          >
            取消
          </div>
        </div>
      </div>
    </div>
  );
}
