import React, { Component } from 'react'

import { Link } from 'react-router-dom'
import loginCss from './login.module.css'

export default class Login extends Component {
  //初始化状态
  state = ({
    name:'',
    password:''
  })
  login = () => {
    const { name, password } = this.state
      console.log(name, password);
  }
    //判断注册信息是否合法
    isLegal = () =>{
      if(
        this.state.name.search(/\s/) === -1 &&
        this.state.password.search(/\s/) === -1 &&
        this.state.ensurePassword.search(/\s/) === -1 &&
        this.state.password === this.state.ensurePassword
      ){
        return true
      }
      // else if(//判断输入栏是否为空
      //   this.state.name.length > 0 &&
      //   this.state.password.length > 0 &&
      //   this.state.ensurePassword.length > 0
      // ){
      //   return true
      // }
      else{
        return false
      }
    }
    //提示函数
    tip(tips){
      let tipNode = document.createElement('div')
      tipNode.innerText = tips
      document.body.appendChild(tipNode)
      tipNode.style.animation = 'tips 1.5s linear'
      tipNode.classList.add('tip')
      //等待时间后 移除提示
      setTimeout(()=>{
        document.body.removeChild(tipNode)
      },1500)
    }
  toRegister = () => {
    console.log(this);
    // this.props.history.push('/register')
  }
  //边框样式切换
  iptOnFocus = (e) => {
    e.target.style.borderColor = 'black'
  }
  iptOnBlur = (e) => {
    console.log(e.target);
    e.target.style.borderColor = 'rgb(91, 130, 248)'
  }
  render() {
    return (
      <div className={loginCss.container}>
        <div className={loginCss.iptWraper}>
          <div className={loginCss.iptInfo}>昵称</div>
          <div><input onFocus={this.iptOnFocus} onBlur={this.iptOnBlur} onChange={(e)=>{this.setState({name:e.target.value})}} className={loginCss.ipt} type="text"/></div>
        </div>
        <div className={loginCss.iptWraper}>
          <div className={loginCss.iptInfo}>密码</div>
          <div><input onFocus={this.iptOnFocus} onBlur={this.iptOnBlur} onChange={(e)=>{this.setState({password:e.target.value})}} className={loginCss.ipt} type="password"/></div>
        </div>
        <div className={loginCss.loginBtn}>
          <div>登录</div>
        </div>
        <div className={loginCss.toRegister}>
          您还没有账户?
          <Link to='/register' style={{ textDecoration:'none'}}>
            <span onClick={this.toRegister}>注册</span>

          </Link>
        </div>
      </div>
    )
  }
}
